properties.entry:
BEGIN
EXECUTE IMMEDIATE 'DROP USER ${sql.username} CASCADE';
EXCEPTION WHEN OTHERS THEN
IF SQLCODE != ${sql.oracle.error.userDoesNotExist} THEN
RAISE;
END IF;
END;
/

CREATE USER ${sql.username}
IDENTIFIED BY ${sql.password}
DEFAULT TABLESPACE ${sql.oracle.defaultTablespace}
TEMPORARY TABLESPACE ${sql.oracle.temporaryTablespace}
QUOTA ${sql.oracle.quota} ON ${sql.oracle.defaultTablespace}
/

GRANT CREATE PROCEDURE
, CREATE SEQUENCE
, CREATE SESSION
, CREATE SYNONYM
, CREATE TABLE
, CREATE TRIGGER
, CREATE TYPE
, CREATE VIEW
TO ${sql.username}
/
